command:
for (const _actor of game.actors) {
  if (!_actor) { continue }
  if (_actor.type !== 'Player') { continue }
  _actor.update({
    "token.name": _actor.name,
    "token.displayName": CONST.TOKEN_DISPLAY_MODES.HOVER,
    "token.img": _actor.img,
    "token.bar1": {
      attribute: "attributes.hp"
    },
    "token.bar2": {
      attribute: "attributes.ac.value"
    },
    "token.displayBars": CONST.TOKEN_DISPLAY_MODES.ALWAYS,
    "token.vision": true,
    "token.actorLink": true
  })
}
